        expr = 3*D1*D1d*Hd**2*OMd*baseint.pk(k1/G)*(-2*H*f*xd*(2*G**2*(8*H - Hd*fd + Hd) - k1**2*xd**2*(7*H - Hd*fd + Hd))*(Qm + xQm - 2) + 2*H*k1**2*xd**3*(b1*(Qm - 1) + xb1*(xQm - 1))*(H - Hd*fd + Hd) + Hd*d**4*k1**2*(fd - 1)*(-2*H**2*Qm*f + H**2*be*f - 2*H**2*f*xQm + H**2*f*xbe - 2*H**2*xQm*xb1 + H**2*xb1*xbe - 2*Hp*f - Hp*xb1 + b1*(-2*H**2*Qm + H**2*be - Hp)) + d**3*k1**2*(Hd*(fd - 1)*(b1*(-3*H**2*xd*(-2*Qm + be) + 2*H*(Qm - 1) + 3*Hp*xd) + xb1*(-3*H**2*xd*(-2*xQm + xbe) + 2*H*(xQm - 1) + 3*Hp*xd)) + f*(H**2*(-3*Hd*be*xd*(fd - 1) + 6*Hd*fd*xQm*xd - 3*Hd*fd*xbe*xd - 6*Hd*xQm*xd + 3*Hd*xbe*xd + Qm*(6*Hd*xd*(fd - 1) - 4) - 4*xQm + 8) + 2*H*Hd*(fd - 1)*(Qm + xQm - 2) + 6*Hd*Hp*xd*(fd - 1))) + d**2*(-H**2*Hd*be*(fd - 1)*(2*G**2*f - 3*b1*k1**2*xd**2 - 3*f*k1**2*xd**2) + f*(2*G**2*Hd*(fd - 1)*(H**2*(2*Qm + 2*xQm - xbe) + 2*Hp) + k1**2*xd*(H**2*(3*Hd*xd*(fd - 1)*(-2*xQm + xbe) + Qm*(-6*Hd*xd*(fd - 1) + 22) + 22*xQm - 44) - 6*H*Hd*(fd - 1)*(Qm + xQm - 2) - 6*Hd*Hp*xd*(fd - 1))) + k1**2*xd*(b1*(H**2*(Qm*(-6*Hd*xd*(fd - 1) + 2) - 2) - 6*H*Hd*(Qm - 1)*(fd - 1) - 3*Hd*Hp*xd*(fd - 1)) + xb1*(H**2*(3*Hd*xd*(fd - 1)*(-2*xQm + xbe) + 2*xQm - 2) - 6*H*Hd*(fd - 1)*(xQm - 1) - 3*Hd*Hp*xd*(fd - 1)))) + d*(f*(-2*G**2*(H**2*(-Hd*be*xd*(fd - 1) + 2*Hd*fd*xQm*xd - Hd*fd*xbe*xd - 2*Hd*xQm*xd + Hd*xbe*xd + 2*Qm*(Hd*xd*(fd - 1) - 2) - 4*xQm + 8) + 2*H*Hd*(fd - 1)*(Qm + xQm - 2) + 2*Hd*Hp*xd*(fd - 1)) + k1**2*xd**2*(H**2*(-Hd*be*xd*(fd - 1) + 2*Hd*fd*xQm*xd - Hd*fd*xbe*xd - 2*Hd*xQm*xd + Hd*xbe*xd + 2*Qm*(Hd*xd*(fd - 1) - 16) - 32*xQm + 64) + 6*H*Hd*(fd - 1)*(Qm + xQm - 2) + 2*Hd*Hp*xd*(fd - 1))) + k1**2*xd**2*(b1*(H**2*(-Hd*be*xd*(fd - 1) + 2*Qm*(Hd*xd*(fd - 1) - 2) + 4) + 6*H*Hd*(Qm - 1)*(fd - 1) + Hd*Hp*xd*(fd - 1)) + xb1*(H**2*(-Hd*xd*(fd - 1)*(-2*xQm + xbe) - 4*xQm + 4) + 6*H*Hd*(fd - 1)*(xQm - 1) + Hd*Hp*xd*(fd - 1)))))*np.sin(k1*(d - xd)/G)/(H**2*d*k1**5*(d - xd)**4) + 6*D1*D1d*Hd**2*OMd*Pk*(H**2*d**3*k1**2*(b1*(Qm - 1) + f*(Qm + xQm - 2) + xb1*(xQm - 1)) - 2*H*xd*(H*k1**2*xd**2*(b1*(Qm - 1) + xb1*(xQm - 1)) - f*(G**2*(8*H - Hd*fd + Hd) - H*k1**2*xd**2)*(Qm + xQm - 2)) + d**2*(G**2*H**2*Hd*be*f*(fd - 1) - G**2*Hd*f*(fd - 1)*(H**2*(2*Qm + 2*xQm - xbe) + 2*Hp) - 4*H**2*f*k1**2*xd*(Qm + xQm - 2) + 4*H**2*k1**2*xd*(-Qm*b1 + b1 - xQm*xb1 + xb1)) + d*(5*H**2*k1**2*xd**2*(b1*(Qm - 1) + xb1*(xQm - 1)) + f*(G**2*(H**2*(-Hd*be*xd*(fd - 1) + 2*Hd*fd*xQm*xd - Hd*fd*xbe*xd - 2*Hd*xQm*xd + Hd*xbe*xd + 2*Qm*(Hd*xd*(fd - 1) - 2) - 4*xQm + 8) + 2*H*Hd*(fd - 1)*(Qm + xQm - 2) + 2*Hd*Hp*xd*(fd - 1)) + 5*H**2*k1**2*xd**2*(Qm + xQm - 2))))*np.cos(k1*(d - xd)/G)/(G*H**2*d*k1**4*(d - xd)**3)

